import React, { useEffect, useState } from 'react'
import Appdata from './AppData'
import "./style.css"
import AppDetail from './AppDetail';
import Cards from './Cards'
import MockData from './MockData';
import gplayLogo from './logo.png'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Search = () => {

    const [data, setData] = useState([{}]);

    const navigate = useNavigate();
    const handleClick = (element) => {
        console.log(navigate);
        navigate(`/apps/${element.apppackage}`);
        console.log(element);
    }

    const handleScraping = ()=> {
        axios.get(`http://127.0.0.1:5000/`)
        .then(res => {
            console.log(res)
        })
    }
    useEffect(() => {
        fetch('/').then(
            response => response.json()
        ).then(data => {
            setData(data)
            console.log(data)
        })
    }, []);

    return (
        <>
            <div className="container d-flex justify-content-between align-items-center">
                <img src={gplayLogo} style={{ width: "20rem", position: "relative", left: "38%", cursor: "pointer" }} alt="" onClick={handleScraping}/>
            </div>
            <section className='iteam_section mt-4 container'>
                <div className="row mt-2 d-flex justify-content-around align-items-center">
                    <Cards data={Appdata} onCardClick={(element)=>handleClick(element)} />
                </div>
            </section>
        </>
    )
}

export default Search
