import React, { useEffect, useState } from "react";
import Card from 'react-bootstrap/Card'
import { useParams } from "react-router-dom";
import gplayLogo from './logo.png'
import axios from "axios";
import AppScreen from "./AppScreen";

const AppDetail = () => {
    useEffect(() => {
        getAppWithAxios();
      }, []);
    let { id } = useParams();
    const [data, setData] = useState([{}]);
    const getAppWithAxios = async () => {
        const response = await axios.get(`http://localhost:8080/apps/?app_id=${id}`);
        setData((response.data)[0]);
        console.log(data.screenshots);
    };

    return (
        <>
            <div className="container d-flex justify-content-between align-items-center">
                <img src={gplayLogo} style={{ width: "20rem", position: "relative", left: "38%", cursor: "pointer" }} alt="" />
            </div>
            <div className="container justify-content-between align-items-center">
                <h1 className="container upper_data h4">App Details for {data.appname}</h1>
            <Card style={{ width: '18rem', border: "none" }} className="container hove mb-4 align-items-center">
                <Card.Img variant="top" className='cd' src={data.appimg} style={{width:'12rem',height:'12rem'}}/>
            </Card>
                <div className="card_body">
                    <div>
                        {/* <h4 className='mt-2'>{data.appname}</h4> */}
                        <h1 className="container upper_data h4">App Rating</h1>
                        <span className="card_body">{data.rating}&nbsp;â˜…</span>
                    </div>
                </div>

                
            
                <div className="container">
                    <h3>App Package</h3>
                    <p>{data.apppackage}</p>
                </div>
                <div className="container">
                    <h3>App Developer</h3>
                    <p>{data.developer}</p>
                </div>
                <div className="row mt-2 d-flex justify-content-around align-items-center">
                    <h3>App Screenshots</h3>
                    {/* <AppScreen data={data.screenshots}/> */}
                </div>
            </div>
        </>
    )
}

export default AppDetail
