import React, { useEffect, useState } from "react";
import Card from 'react-bootstrap/Card'
import { useParams } from "react-router-dom";
import gplayLogo from './logo.png'
import axios from "axios";

const AppDetail = () => {

    let { id } = useParams();
    const [data, setData] = useState([{}]);
    const getAppWithAxios = async () => {
        const response = await axios.get(`http://localhost:8080/apps/?app_id=${id}`);
        setData((response.data)[0]);
        console.log(response.data);
    };

    useEffect(() => {
        getAppWithAxios();
      }, []);

    return (
        <>
            <div className="container d-flex justify-content-between align-items-center">
                <img src={gplayLogo} style={{ width: "20rem", position: "relative", left: "38%", cursor: "pointer" }} alt="" />
            </div>
            <Card style={{ width: '22rem', border: "none" }} className="hove mb-4">
                <Card.Img variant="top" className='cd' src={data[0].appimg} />

                <div className="card_body">
                    <div className="upper_data d-flex justify-content-between align-items-center">
                        <h4 className='mt-2'>{id}</h4>
                        {/* <span>{id.rating}&nbsp;â˜…</span> */}
                    </div>

                    {/* <div className="last_data d-flex justify-content-between align-items-center">
                                        <p>{element.developer}</p>
                                    </div>
                                    <Screenshots data={element.screenshots}/> 
                                </>*/}</div>
            </Card>
        </>
    )
}

export default AppDetail
